/**
 * Auth block
 */

/**
 * SECRET_KEY contains key for hashing in authorisation
 */
exports.SECRET_KEY = "This is my secret";

/**
 * Tech block
 */

/**
 * PATH_LOG parameters:
 * console
 * file as path to it
 *
 * MODE parameters:
 * develop (return full stack of messages)
 * production (return only info & errors)
 */
exports.PATH_LOG = 'console';
exports.MODE = 'develop';


/**
 * Tech functions
 */
exports.secret = function(value) {
  SECRET_KEY = value;
}

exports.path = function(value) {
  PATH_LOG = value;
}

exports.mode = function(value) {
  MODE = value;
}